mapper.select:
SELECT COUNT(1) FROM ls_admin WHERE `status` >= 0

	AND `name`=#{name}


	AND phone=#{phone}

SELECT
a.adminid,a.clinicid,a.loginame,a.`name`,a.phone,a.`status`,a.createtime,a.logintime,b.shortname
clinicname,a.caller_nos as callerNos,a.transfer_no as transferNo,a.org_id as orgId,a.org_name as orgName ,
a.no_wx as noWx,a.micro_userid as microUserId
FROM ls_admin a
LEFT JOIN ls_clinic b ON
a.clinicid=b.clinicid WHERE a.`status` >= 0

	AND a.`name`=#{name}


	AND a.phone=#{phone}

ORDER BY a.adminid DESC LIMIT #{index},#{limit}

SELECT adminid,`name` FROM ls_admin WHERE `status`=1
AND adminid IN (SELECT adminid FROM ls_admin_role WHERE roleid=#{roleid})
AND clinicid=#{clinicid}

SELECT adminid,`name` FROM ls_admin

	`status`=1 
	
		AND adminid IN 
		
			#{item}
		
	
	
		AND adminid = #{adminid}
	
	
		AND clinicid=#{clinicid}
	
	
		AND org_id=#{orgid}

SELECT adminid from ls_admin a
JOIN ls_org o on a.org_id=o.id
 where o.parent_ids like  CONCAT('%,',#{parentId},',%') or o.id=#{parentId}

SELECT adminid from ls_admin a
JOIN ls_org o on a.org_id=o.id
 where o.id=#{id} or o.id=#{parentId}

SELECT a.adminid, a.loginame, a.name FROM ls_admin a JOIN ls_admin_role ar on a.adminid=ar.adminid JOIN ls_role r ON ar.roleid=r.roleid AND r.roleid=3
and a.adminid in #{aid}
and a.`name` like CONCAT('%',#{adminName,jdbcType=VARCHAR},'%' )
order by a.createtime desc
limit ${index} , ${limit}

SELECT count(*) FROM ls_admin a JOIN ls_admin_role ar on a.adminid=ar.adminid JOIN ls_role r ON ar.roleid=r.roleid AND r.roleid=3
and a.adminid in #{aid}
and a.`name` like CONCAT('%',#{adminName,jdbcType=VARCHAR},'%' )

mapper.update:
update ls_admin
set ec_userid = #{ecUserId}
where phone =#{phone}
