mapper.select:
SELECT * FROM ls_bag_order WHERE ((`status`=1 AND
paytype=1) OR paytype=2) AND doctorid=#{doctorid}
ORDER BY createtime
DESC LIMIT #{index},#{limit}

SELECT a.*,b.`name`,b.phone FROM ls_bag_order a LEFT JOIN ls_doctor_user b ON a.doctorid=b.doctorid WHERE ((a.`status`=1 AND a.paytype=1) OR a.paytype=2)
AND b.phone=#{phone}
ORDER BY a.createtime DESC LIMIT #{index},#{limit}
