mapper.select:
SELECT COUNT(1) FROM ls_product WHERE `status` >= 0

	AND classid=#{classid}


	AND title LIKE CONCAT('%',#{title},'%')

SELECT a.pid,a.title,a.image,a.costprice,a.price,a.`status`,a.createtime,b.tagname classname FROM ls_product a LEFT JOIN ls_worktag b ON a.classid=b.tagid WHERE a.`status` >= 0
AND a.classid=#{classid}
AND a.title LIKE CONCAT('%',#{title},'%')
LIMIT #{index},#{limit}
